} catch (PDOException $e) {
    echo "Lỗi kết nối: " . $e->getMessage();
}

// Hiển thị thông tin CV
function getCV($conn) {
    $query = "SELECT * FROM personal_info";
    $stmt = $conn->query($query);
    $personalInfo = $stmt->fetch(PDO::FETCH_ASSOC);

    $query = "SELECT * FROM experience";
    $stmt = $conn->query($query);
    $experience = $stmt->fetchAll(PDO::FETCH_ASSOC);

    $query = "SELECT * FROM education";
    $stmt = $conn->query($query);
    $education = $stmt->fetchAll(PDO::FETCH_ASSOC);

    $query = "SELECT * FROM skills";
    $stmt = $conn->query($query);
    $skills = $stmt->fetchAll(PDO::FETCH_ASSOC);

    return [
        'personalInfo' => $personalInfo,
        'experience' => $experience,
        'education' => $education,
        'skills' => $skills
    ];
}

// Thêm mới nội dung
function addContent($conn, $table, $data) {
    $columns = implode(", ", array_keys($data));
    $placeholders = ":" . implode(", :", array_keys($data));
    $query = "INSERT INTO $table ($columns) VALUES ($placeholders)";
    $stmt = $conn->prepare($query);
    $stmt->execute($data);
}

// Chỉnh sửa nội dung
function updateContent($conn, $table, $data, $id) {
    $setClause = "";
    foreach ($data as $key => $value) {
        $setClause .= "$key = :$key, ";
    }
    $setClause = rtrim($setClause, ", ");
    $query = "UPDATE $table SET $setClause WHERE id = :id";
    $stmt = $conn->prepare($query);
    $data['id'] = $id;
    $stmt->execute($data);
}

// Xóa nội dung
function deleteContent($conn, $table, $id) {
    $query = "DELETE FROM $table WHERE id = :id";
    $stmt = $conn->prepare($query);
    $stmt->execute(['id' => $id]);
}

